select reminder_instances.id, user_id, reminder_template, start_date, start_hour, start_min, end_date, end_hour, end_min, reminder_instances.is_deleted as reminder_is_deleted, email, mobile_phone, text_notify, email_notify, in_app_notify, users.is_deleted as user_is_deleted, type, user_name, first_name, last_name from reminder_instances 
join users on users.id = reminder_instances.user_id
where start_date = $1 AND start_hour = $2 AND start_min = $3;